export declare const cssTemplate = "body {\n    font-family: __BIBLICAL_FONT__, Georgia, serif;\n    line-height: 1.5;\n    margin: 2rem;\n    max-width: 800px;\n    margin-left: auto;\n    margin-right: auto;\n    font-size: 2rem;\n    user-select: none;\n}\n\nhtml[lang='hbo'] {\n    direction: rtl;\n}\n\nhtml[lang='grc'] {\n    direction: ltr;\n}\n.chapter {\n    margin-bottom: 1rem;\n}\n.chapter-number {\n    font-family: __PROJECT_FONT__;\n    font-weight: bold;\n    margin-right: 0.5rem;\n    color: #444;\n}\n.verse {\n    display: inline;\n}\n.verse-number {\n    font-family: __PROJECT_FONT__;\n    font-size: 1.5rem;\n    color: black;\n    vertical-align: top;\n    margin-left: 0.3rem;\n}\n.annotation-toggle {\n    font-family: __PROJECT_FONT__;\n    cursor: pointer;\n    color: gray;\n    text-decoration: none;\n    vertical-align: super;\n    font-size: 1.5rem;\n    position: relative;\n    display: inline-block;\n}\n\n.annotation {\n    font-family: __PROJECT_FONT__;\n\n    color: black;\n    display: block;\n    position: absolute;\n    left: 50%;\n    transform: translateX(-50%); /* Adjust for centering */\n    background-color: #f9f9f9;\n    border: 1px solid #ccc;\n    padding: 0.5rem;\n    z-index: 10;\n    \n    white-space: normal; /* Allow wrapping */\n    word-wrap: break-word; /* Ensure long words wrap */\n    max-width: 100vw; /* Prevent width from exceeding the viewport */\n    box-sizing: border-box; /* Include padding and border in width calculation */\n    overflow-wrap: break-word; /* Break long words if necessary */\n}\n.annotation.hidden {\n    display: none;\n}\n\ndiv[dir='rtl'] .parsing:not(:last-child), \ndiv[dir='rtl'] .lexical-form:not(:last-child), \ndiv[dir='rtl'] .gloss:not(:last-child) \n{\n    margin-left: 0.5rem;\n}\n\ndiv[dir='ltr'] .parsing:not(:last-child), \ndiv[dir='ltr'] .lexical-form:not(:last-child), \ndiv[dir='ltr'] .gloss:not(:last-child) \n{\n    margin-right: 0.5rem;\n}\n\n.gloss {\n    font-style: italic;\n}\n\n.lexical-form {\n    font-family: __BIBLICAL_FONT__, Georgia, serif;\n}\n\n.wd {\n    white-space: nowrap;\n}\n\n/* Mostly motivated by the TOC */\nh1 {\n    font-size: 1.5em;\n}\n\nh2 {\n    font-size: 1em;\n}\n\ntable.toc {\n    width: 100%;\n    border-collapse: collapse;\n    margin: 20px 0;\n    font-size: 16px;\n    text-align: left;\n    background-color: #f9f9f9;\n    border: 1px solid #ddd;\n    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);\n  }\n  \n  .toc th, .toc td {\n    padding: 12px 15px;\n    border: 1px solid #ddd;\n  }\n  \n  .toc th {\n    font-weight: normal;\n  }\n  \n  .toc td {\n    text-align: center;\n  }\n    \n  .toc tr:nth-child(even) {\n    background-color: #f2f2f2;\n  }\n  \n  .toc tr:hover {\n    background-color: #e9e9e9;\n  }\n  \n  .toc a {\n    color: #007bff;\n    text-decoration: none;\n    font-weight: bold;\n  }\n  \n  .toc a:hover {\n    text-decoration: underline;\n  }\n  \n  .toc h2 {\n    font-size: 20px;\n    margin-bottom: 10px;\n    color: #333;\n  }\n  \n  /* Responsive Design */\n  @media (max-width: 600px) {\n    .toc {\n      font-size: 14px;\n    }\n  \n    .toc th, .toc td {\n      padding: 10px;\n    }\n  }\n\nul.chapters > li {\n\tlist-style: none;\n}\n\nul.chapters > li > a {\n    color: #000;\n    text-decoration: none;\n    font-weight: bold;\n}";
